mapper.select:

select * from (select writer_image_up.*, row_number() over(order by img_seq ) as rown from writer_image_up where REGEXP_LIKE (tag, #{tag})


	and REGEXP_LIKE (tag, #{keyword2})


	and file_extension = #{file_extension}


	and usage = #{usage}


	order by ${orderBy} desc


) where rown between #{start} and #{end}
